import React from "react";
import { useState } from "react";

const LoginButton = ({ loginAction }) => {
  return <button onClick={loginAction}> Login </button>;
};

const LogoutButton = ({ logoutAction }) => {
  return <button onClick={logoutAction}> Logout </button>;
};

export default function OpcionalRender() {
  const [access, setAccess] = useState(true);
  const [nMessages, setNMessages] = useState(0);

  const logoutAction = () => {
    setAccess(false);
  };

  const loginAction = () => {
    setAccess(true);
  };

  let optionalButton;

  if (access) optionalButton = <LogoutButton logoutAction={logoutAction} />;
  else optionalButton = <LoginButton loginAction={loginAction} />;

  let addMessages = () => {
    setNMessages(nMessages + 1);
  };

  return (
    <div>
      {optionalButton}
      {nMessages > 0 && <p>You have {nMessages} new messages</p>}
      {nMessages === 1 && <p>You have {nMessages} new messages</p>}
      {nMessages === 0 && <p>There are no new messages</p>}
      <button onClick={addMessages}>New Message</button>
    </div>
  );
}
