import React, { useRef } from "react";
import PropTypes from "prop-types";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";

import { LEVELS } from "../../models/levels.enum";
import { Task } from "../../models/task.class";

const TaskFormik = ({ add, length }) => {
  const initialTask = {
    name: "",
    description: "",
    level: LEVELS.NORMAL,
    completed: false,
  };

  let task = new Task();

  const TaskSchema = Yup.object().shape({
    name: Yup.string()
      .min(3, "El nombre de la tarea es demasiado corto")
      .max(100, "El nombre de la tarea es demasiado Largo")
      .required("Usuario es requerido"),
    description: Yup.string(),
    level: Yup.string()
      .oneOf(
        [LEVELS.NORMAL, LEVELS.BLOCKING, LEVELS.URGENT],
        "Debe seleccionar: Normal / Urgent / Blocking."
      )
      .required("El nivel es requerido."),
  });

  const nameRef = useRef("");
  const descriptionRef = useRef("");
  const levelRef = useRef(LEVELS.NORMAL);

  function addTask(e) {
    e.preventDefault();
    const newTask = new Task(
      nameRef.current.value,
      descriptionRef.current.value,
      false,
      levelRef.current.value
    );
    add(newTask);
  }

  const normalStyle = {
    color: "blue",
    fontWeight: "bold",
  };

  const UrgentStyle = {
    color: "yellow",
    fontWeight: "bold",
  };

  const blockingStyle = {
    color: "tomato",
    fontWeight: "bold",
  };

  return (
    <div>
      <Formik
        initialValues={initialTask}
        validationSchema={registerSchema}
        onSubmit={async (values) => {
          await new Promise((resolve, reject) => {
            setTimeout(resolve, 500);
          });
          submit();
        }}
        // {async (values) => {
        //   await new Promise((r) => {
        //     setTimeout(r, 500);
        //   });
        //   alert(JSON.stringify(values, null, 2));
        //   localStorage.setItem("credentials", values);
        // }}
      >
        {/* We obtain props from formik */}
        {(props) => {
          const {
            values,
            touched,
            errors,
            isSubmitting,
            handleChange,
            handleSubmit,
            handleBlur,
          } = props;

          return (
            <Form style={{ display: "flex", flexDirection: "column", gap: 1 }}>
              <label htmlFor="username">Username: </label>
              <Field
                id="username"
                type="text"
                name="username"
                placeholder="Arnold"
              />
              {errors.username && touched.username && (
                <ErrorMessage component="div" name="username">
                  {(msg) => <p>{msg}</p>}
                </ErrorMessage>
              )}

              <label htmlFor="email">Email: </label>
              <Field
                id="email"
                type="email"
                name="email"
                placeholder="example@email.com"
              />
              {errors.email && touched.email && (
                <ErrorMessage component="div" name="email">
                  {(msg) => <p>{msg}</p>}
                </ErrorMessage>
              )}

              <label htmlFor="password">Password: </label>
              <Field
                id="password"
                name="password"
                type="password"
                placeholder="password"
              />
              {errors.password && touched.password && (
                <div className="error">
                  <ErrorMessage component="div" name="password" />
                </div>
              )}

              <label htmlFor="confirm">Confirm Password: </label>
              <Field
                id="confirm"
                name="confirm"
                type="password"
                placeholder="Confirm Password"
              />
              {errors.confirm && touched.confirm && (
                <div className="error">
                  <ErrorMessage component="div" name="confirm" />
                </div>
              )}

              <button type="submit" style={{ marginTop: "1rem" }}>
                Login
              </button>

              {isSubmitting ? <p>Sending your credentials...</p> : null}
            </Form>
          );
        }}
      </Formik>

      <form
        onSubmit={addTask}
        className="d-flex justify-align-content-center align-items-center mb-4"
      >
        <div className="form-outline flex-fill">
          <input
            ref={nameRef}
            id="inputName"
            type="text"
            required
            autoFocus
            placeholder="Task Name"
            className="form-control form-control-lg"
          />
          <input
            ref={descriptionRef}
            id="inputDescription"
            type="text"
            placeholder="Task Description"
            required
            className="form-control form-control-lg"
          />
          <select
            ref={levelRef}
            defaultValue={LEVELS.NORMAL}
            id="selectLevel"
            className="form-control form-control-lg"
          >
            <option value={LEVELS.NORMAL}>Normal</option>
            <option value={LEVELS.URGENT}>Urgent</option>
            <option value={LEVELS.BLOCKING}>Blocking</option>
          </select>
          <button type="submit" className="btn btn-success btn-lg ms-2">
            {length > 0 && "Add Your Task"}
            {length === 0 && "Create Your First Task"}
          </button>
        </div>
      </form>
    </div>
  );
};

TaskFormik.propTypes = {
  add: PropTypes.func.isRequired,
  length: PropTypes.number.isRequired,
};

export default TaskFormik;
