import React from "react";
import Child from "../pure/child";

const Father = () => {
  function showMessage(text) {
    alert(`message received: ${text}`);
  }

  return (
    <div style="background-color:tomato">
      <Child name="Jeffry" send={showMessage}></Child>
    </div>
  );
};

export default Father;
