import React, { useEffect } from "react";
import PropTypes from "prop-types";
import { Task } from "../../models/task.class";

import "../../styles/task.scss";

const TaskComponent = ({ task }) => {
  useEffect(() => {
    console.log("Tarea creada");
    return () => {
      console.log("Tarea est치 siendo desmontada");
    };
  }, [task]);

  return (
    <tr className="fw-normal">
      <th>
        <span className="ms-2">{task.name}</span>
      </th>
      <td className="align-middle">
        <span>{task.description}</span>
      </td>
      <td className="align-middle">
        <span>{task.level}</span>
      </td>
      <td className="align-middle">
        <span>
          {task.completed ? (
            <i
              className="bi-toggle-on"
              style={{ color: "green", fontWeight: "bold" }}
            ></i>
          ) : (
            <i
              className="bi-toggle-off"
              style={{ color: "grat", fontWeight: "bold" }}
            ></i>
          )}
        </span>
      </td>
      {/* <h2 className="task-name"> Nombre: </h2>
      <h3> Descripci칩n: {task.description}</h3>
      <h4> Nivel: {task.level}</h4>
      <h5> Esta tarea est치: {task.completed ? "COMPLETADA" : "PENDIENTE"}</h5> */}
    </tr>
  );
};

TaskComponent.propTypes = {
  task: PropTypes.instanceOf(Task),
};

export default TaskComponent;
