import React from "react";
import { useState } from "react";

const LoginButton = (props) => {
  return <button onClick={props.loginAction}> Login </button>;
};

const LogoutButton = (props) => {
  return <button onClick={props.logoutAction}> Logout </button>;
};

export default function OpcionalRender() {
  const [access, setAccess] = useState(true);

  const logoutAction = () => {
    setAccess(false);
  };

  const loginAction = () => {
    setAccess(true);
  };

  let optionalButton;

  if (access) optionalButton = <LoginButton logoutAction={logoutAction} />;
  else optionalButton = <LoginButton loginAction={loginAction} />;

  return <div>{optionalButton}</div>;
}
