    const birthday = () => {
        setAge(age)
    }

  return (
    <div>
      <h1>¡Hola {props.name} desde componente funcional!</h1>
      <h2>Tu edad es: {age}</h2>
      {/* <div><button onClick={this.birthday}> Cumplir años </button></div> */}
    </div>
  );
};

SaludoF.propTypes = {
  name: PropTypes.string,
};

export default SaludoF;
